async def days_between(user_id):
    d1 = get_from_date(user_id)
    d1 = datetime.strptime(d1, "%Y-%m-%d %H:%M:%S.%f")
    d2 = str(datetime.now())
    d2 = datetime.strptime(d2, "%Y-%m-%d %H:%M:%S.%f")
    return int(abs((d2 - d1).days))
